package sum.kern;import javax.swing.*;import java.awt.event.*;import java.awt.FontMetrics;/**Die Warnung dient dazu, in einem Fenster Fehlermeldungen auszugeben.@author Bernard Schriek@version 7.5 vom 29.10.2013*/class Warnung extends JDialog implements ActionListener{	JButton hatButton;	JLabel hatLabel1, hatLabel2;	boolean zOk;		protected Warnung(JFrame pFenster, String pMeldung)	{		super(pFenster, "Warnung", true);		this.getContentPane().setLayout(null);		this.setResizable(false);		this.setSize(300, 150);		this.setLocation(200, 100);		hatButton = new JButton("Ok");		hatButton.addActionListener(this);		hatButton.setLocation(120, 70);		hatButton.setSize(60, 30);		this.getContentPane().add(hatButton);		FontMetrics fm = pFenster.getGraphics().getFontMetrics();		int laenge = fm.stringWidth(pMeldung);		hatLabel1 = new JLabel("SuM-Fehler:");		hatLabel1.setLocation(10, 10);		hatLabel1.setSize(280, 20);		this.getContentPane().add(hatLabel1);		hatLabel2 = new JLabel(pMeldung);		hatLabel2.setLocation(5 + (280 - laenge) / 2, 40);		hatLabel2.setSize(280, 20);		this.getContentPane().add(hatLabel2);		zOk = false;		this.setVisible(true);	}		public void actionPerformed(ActionEvent pEvent)	{		zOk = pEvent.getActionCommand().equals("Ok");		this.setVisible(false);		this.dispose();	}		protected boolean istOk()	{		return zOk;	}}