package sum.komponenten;import java.io.*;import javax.swing.*;/**Implementierung der Swingklasse RadioGroup@author Bernard Schriek@version 7.5 vom 29.10.2013*/public class Radiogruppe extends ButtonGroup implements Serializable{		public Radiogruppe()	{		super();	}		public void fuegeEin(Radioknopf pKnopf)	{		this.add((AbstractButton)pKnopf.hatComponent);	}		public void entferne(Radioknopf pKnopf)	{		this.remove((AbstractButton)pKnopf.hatComponent);	}	}